import React, { Component } from 'react';
import axios from 'axios';
import { Form, Input, Button, TextArea, Dropdown } from 'semantic-ui-react';
import { Redirect } from 'react-router-dom';
import './Place.css';


const options = [
    { key: '아파트1', text: '아파트1', value: '아파트1' },
    { key: '아파트2', text: '아파트2', value: '아파트2' }
]

class Write extends Component{
    state = {
        submited_status:false,
        submited_title:"",
        submited_content:"",
        apartment:0,
        oValue:0
    }

    

    handleFormSubmit(_title,_content) {
        this.setState({
            submited_title:_title,
            submited_content:_content
        });
        if(_title==="" || _content===""){
            this.setState({
                submited_status:false
            });
 
        }
    }
    
    render() {
        axios.post('http://127.0.0.1:8000/api/create/$', 
            {title:this.state.submited_title, content:this.state.submited_content})
            .then( res => { console.log(res) } )
            .catch( error => { console.error(error) } );
        
        return (
            
            <div>
                {
                    this.state.submited_status && <Redirect to={{
                        pathname: '/saved',
                        state: { 
                            message:"저장되었습니다",
                            
                        }
                    }}/>
                }
                <h1>write</h1>
                <div className="writeplace">  
                    

                    <Form method="POST"
                        onSubmit= {function(e){
                            e.preventDefault();
                            this.setState({
                                
                                submited_status:true
                            })
                            this.handleFormSubmit(
                                e.target.title.value,
                                e.target.content.value
                            );
                            
                        }.bind(this)}>

                        {/*<div class="ui selection dropdown">
                            <input type="hidden" name="게시판 선택">
                            <i class="dropdown icon"></i>
                            </input>
                            <div class="default text">Gender</div>
                            <div class="menu">
                                    <div class="item" data-value="1">아파트1</div>
                                    <div class="item" data-value="2">아파트2</div>
                            </div>
                    </div>*/}
                            
                        <Form.Field>
                            <Form.Field
                                id='form-input-title'
                                control={Input}
                                placeholder='제목을 입력하세요'
                                name='title'
                                maxLength='20'
                            />
                            <select class="ui dropdown">
                                <option value="">아파트 선택</option>
                                <option value="1">아파트1</option>
                                <option value="2">아파트2</option>
                            </select>

                            <TextArea
                                id='form-input-content'
                                placeholder='Tell us more' 
                                style={{ minHeight: 300 }} 
                                name='content'
                                maxLength='500'
                            />
                        </Form.Field>
                        
                        <Form.Field
                            id='form-button'
                            control={Button} 
                            type="submit"
                            content='저장'
                        />
                    </Form>
                </div>
          
            </div>
        );
    }
}

export default Write;
