import React, { Component } from 'react';
import axios from 'axios';
import './Posts.css';

import { Table, } from 'semantic-ui-react'

class Poststable extends Component{
    
    state = {
        persons: [],
        activePage:1,
        start:0,
        end:10,
        dividedtable:[]
    }
    
    onHandleUp(){
        console.log('onhandleup');
        const {start,end,activePage}=this.state;
        this.setState({
            start:start+10,
            end:end+10,
            activePage:activePage+1

        });
    }

    onHandleDown(){
        console.log('onhandledown');
        const {start,end,activePage}=this.state;
        if(start===0) return;
        this.setState({
            start:start-10,
            end:end-10,
            activePage:activePage-1,
           
        });
    }

        
    async componentDidMount() {
        axios.get('http://127.0.0.1:8000/api')
            .then(res => {
            const persons = res.data;
            this.setState({ persons });
        })
    }
    
    render() {
        
        const tablelist = this.state.persons.map( (person) => 
            (
                <Table.Row >
                    <Table.Cell width='10'>
                        {person.title}
                    </Table.Cell>
                    <Table.Cell>
                        {person.content}
                    </Table.Cell>
                    <Table.Cell collapsing textAlign='right'>
                        {person.date}
                    </Table.Cell>
                </Table.Row>
            )
            );
        
            //const array=[];
            const array=this.state.persons.slice(this.state.start,this.state.end); 
            const ttlist = array.map( (a) => 
                (
                    <Table.Row >
                        <Table.Cell width='10'>
                            {a.title}
                        </Table.Cell>
                        <Table.Cell>
                            {a.content}
                        </Table.Cell>
                        <Table.Cell collapsing textAlign='right'>
                            {a.date}
                        </Table.Cell>
                    </Table.Row>
                )
            );
            
        return(
            <div>
                <button onClick={function(e) {
                    e.preventDefault();
                    this.onHandleDown()
                }.bind(this)}>
                    ◁
                </button>
                <button onClick={function(e) {
                    e.preventDefault();
                    this.onHandleUp()
                }.bind(this)}>
                    ▷
                </button>

                <Table celled striped >
                    <Table.Header>
                    <Table.Row >
                        <Table.HeaderCell colSpan='5'>posts</Table.HeaderCell>
                    </Table.Row>
                    </Table.Header>

                    <Table.Body>
                        {ttlist}
                    </Table.Body>
                </Table>
            </div>
        );
    }
}

export default Poststable;
