import React, { Component } from 'react';
import Poststable from './Poststable';
import { Redirect } from 'react-router-dom';
import './Place.css';

class Posts extends Component{
    state={
        mode:"read",
        back:true,
        id:null,
        title:null,
        content:null
    }
    
    render() { 
        return (
            <div className="full">   
                <h1>posts</h1>
                <div className="place2">
                    {   this.state.mode==="read" && 
                        <Poststable onDetailShow={function(_id,_title,_content){
                            this.setState({
                                mode:"update",
                                id:_id,
                                title:_title,
                                content:_content
                            });
                        }.bind(this)}/> 
                    }

                    {
                        this.state.mode==="update" && 
                        <Redirect to={{
                            pathname: '/detail',
                            state: { id: this.state.id ,title:this.state.title ,content:this.state.content}
                        }}
                        />
                    }
                </div>
                
            </div>
        );
    }
}
export default Posts;
