import React, { Component } from 'react';
import axios from 'axios';

import { Table } from 'semantic-ui-react'
import { Button } from 'semantic-ui-react'
class Poststable extends Component{
    
    state = {
        persons: [],
        reversed_persons:[],
        activePage:1,
        start:0,
        end:10,
    }
    
    onHandleUp(){
        const {start,end,activePage}=this.state;
        if (end < this.state.persons.length/10*10) {
            this.setState({
                start:start+10,
                end:end+10,
                activePage:activePage+1
            });
        }
    }

    onHandleDown(){
        const {start,end,activePage}=this.state;
        if(start===0) return;
        this.setState({
            start:start-10,
            end:end-10,
            activePage:activePage-1
        });
    }

    async componentDidMount() {
        axios.get('http://127.0.0.1:8000/api')
            .then(res => {
            const p = res.data;
            this.setState({ persons:p });
            const v=this.state.persons.reverse();
            this.setState({
                persons:v
            });
        });
        
    }
    
    render() {
        const array=this.state.persons.slice(this.state.start,this.state.end);
        const listtable = array.map( (a) => 
            (
                <Table.Row >
                    <Table.Cell width='7'>
                        {a.title}
                    </Table.Cell>
                    <Table.Cell width='10'>
                        {a.content}
                    </Table.Cell>
                    <Table.Cell  collapsing='true' textAlign='middle'>
                        {a.date}
                    </Table.Cell>
                </Table.Row>
            )
        );
        return(
            <div>
                <Table celled striped >
                    <Table.Header>
                    <Table.Row >
                        <Table.HeaderCell colSpan='5'>글 목록</Table.HeaderCell>
                    </Table.Row>
                    </Table.Header>

                    <Table.Body>
                        {listtable}
                    </Table.Body>
                </Table>
                <Button onClick={function() {
                    this.onHandleDown()
                }.bind(this)}>
                    ◁
                </Button>
                <Button onClick={function() {
                    this.onHandleUp()
                }.bind(this)}>
                    ▷
                </Button>
            </div>
        );
    }
}

export default Poststable;
