import React, { Component } from 'react';
import { Button, Form, Input, TextArea } from 'semantic-ui-react';

class Update extends Component{
    state={
        updated_status:false
    }
    handleFormUpdate(_title,_content) {
        this.setState({
            updated_title:_title,
            updated_content:_content
        });
        if(_title==="" || _content===""){
            this.setState({
                updated_status:false
            });
 
        }
    }
    render() {
        
        /*axios.post('http://127.0.0.1:8000/api/create/$', 
            {title:this.state.submited_title, content:this.state.submited_content})
            .then( res => { console.log(res) } )
            .catch( error => { console.error(error) } );*/

        return (
            <div>
                <h1>update</h1>
                <div className="writeplace">  
                    <Form method="POST"
                        onSubmit= {function(e){
                            e.preventDefault();
                            this.setState({
                                
                                updated_status:true
                            })
                            this.handleFormUpdate(
                                e.target.title.value,
                                e.target.content.value
                            );
                            
                        }.bind(this)}>

                        <Form.Field>
                            <Form.Field
                                id='form-input-title'
                                control={Input}
                                name='title'
                                maxLength='20'
                            ></Form.Field>

                            <TextArea
                                id='form-input-content'
                                placeholder='Tell us more' 
                                style={{ minHeight: 300 }} 
                                name='content'
                                maxLength='500'
                            >{this.props.location.state.content}</TextArea>
                        </Form.Field>
                        
                        <Form.Field
                            id='form-button'
                            control={Button} 
                            type="submit"
                            content='저장'
                        />
                    </Form>
                </div>
            </div>
        );
    }
}

export default Update;
