import React, { Component } from 'react';
import Poststable from './Poststable';
import Detail from './Detail';
import { Redirect } from 'react-router-dom';
import './Place.css';

class Posts extends Component{
    state={
        bar:"posts",
        mode:"read",
        back:false,
        id:null,
        title:null,
        content:null
    }

    render() { 
        return (
            <div>   
                <h1>{this.state.bar}</h1>
 
                <div className="place">
{/*                     
                        <Poststable onDetailShow={function(_id,_title,_content){
                            this.setState({
                                mode:"update",
                                bar:"detail",
                                id:_id,
                                title:_title,
                                content:_content
                            });
                           
                            <Redirect to={{
                                pathname: '/detail',
                                state: { id: _id,
                                    title: _title,
                                    content: _content
                                }
                            }}/>

                        }.bind(this)}/>
*/}                        
                    {
                        !this.state.back && this.state.mode==="read" && 
                        <Poststable onDetailShow={function(_id,_title,_content){
                            this.setState({
                                mode:"update",
                                back:true,
                                bar:"detail",
                                id:_id,
                                title:_title,
                                content:_content
                            });
                        }.bind(this)}/> 
                    }

                    {
                        this.state.back && this.state.mode==="update" && 
                        <Detail id={this.state.id} title={this.state.title} content={this.state.content} 
                            test={function(){
                                console.log(1234);
                                this.setState({
                                    mode:"read",
                                    back:false
                                });
                            } .bind(this)}/>
                    }

                </div>
                
            </div>
        );
    }
}
export default Posts;
