import React, { Component } from 'react';
import { Divider, Segment, Button } from 'semantic-ui-react';
import { NavLink } from 'react-router-dom';
import './Place.css';
class Detail extends Component {
    render() {
        const detailcontent = this.props.location.state.content.split(/[\r\n]/).map((partial, i) =>
            partial && <span key={i}>{partial}{i !== this.props.location.state.content.length - 1 && <br />}</span>
        )
        console.log(this.props.location.state.content);
        console.log(detailcontent);
        return (
            <div >
                <h1>detail</h1>
                {detailcontent}

                <Segment className='tableplace'>
                    {/*<Header as='h3'>Title</Header>*/}
                    <h4 align='left'>title</h4>
                    <h2 align='left'>{this.props.location.state.title}</h2>

                    <Divider section />

                    {/*<Header as='h3'>Content</Header>*/}
                    <h4 align='left'>content</h4>
                    
                    <h2 align='left'>{detailcontent}</h2>
                </Segment>
                <NavLink to='/posts'>
                    <Button >목록으로 돌아가기</Button>
                </NavLink>
            </div>

            
        );
    }
}

export default Detail;
