import React, { Component } from 'react';
//import {Field} from "redux-form";
import { Form, Input, Button, TextArea } from 'semantic-ui-react';
import './Place.css';
class Write extends Component{
    state = {
        submited_title:"",
        submited_content:""
    }

    onSubmit(_title,_content) {
        this.setState({
            submited_title:_title,
            submited_content:_content
        })
    }

    render() {
        console.log(this.state.submited_title);
        console.log(this.state.submited_content);
        return (
            <div>
                <h1>write</h1>
                <div className="place">
                    <Form action="/" method="post"
                        onSubmit={function(e){
                        e.preventDefault();
                        this.onSubmit(
                            e.target.title.value,
                            e.target.content.value
                        );
                        }.bind(this)}>
                        <Form.Field>
                            <Form.Field
                                id='form-input-title'
                                control={Input}
                                placeholder='제목을 입력하세요'
                                name='title'
                            />

                            <TextArea 
                                id='form-input-content'
                                placeholder='Tell us more' 
                                style={{ minHeight: 300 }} 
                                name='content'
                            />

                        </Form.Field>
                        
                        <Form.Field
                            id='form-button'
                            control={Button}
                            content='저장'
                            
                            /*onSubmit={function(e) {
                                this.onSubmit(
                                    e.target.title.value,
                                    e.target.content.value
                                );
                            }.bind(this)}*/
                        />
                            
                    </Form>
                </div>
            
                {/*<form action="/create_process" method="post"
            onSubmit={function(e){
              e.preventDefault();
              this.props.onSubmit(
                e.target.title.value,
                e.target.desc.value
