import React, { Component } from 'react';
import Poststable from './Poststable';
import { Redirect } from 'react-router-dom';
import './Place.css';

class Posts extends Component{
    state={
        bar:"posts",
        mode:"read",
        back:true,
        id:null,
        title:null,
        content:null
    }
    async set() {
        this.setState({mode:"read"});
    }
    
    
    render() { 
        
        return (
            <div>   
                <h1>{this.state.bar}</h1>
 
                <div className="place">
                    
                    {   this.state.mode==="read" && 
                        <Poststable onDetailShow={function(_id,_title,_content){
                            this.setState({
                                mode:"update",
                                bar:"detail",
                                id:_id,
                                title:_title,
                                content:_content
                            });
                        }.bind(this)}/> 
                    }

                    {
                        this.state.mode==="update" && 
                        //<Detail id={this.state.id} title={this.state.title} content={this.state.content} />
                        <Redirect to={{
                            pathname: '/detail',
                            state: { id: this.state.id ,title:this.state.title ,content:this.state.content}
                        }}
                        />
                    }
                </div>
                
            </div>
        );
    }
}
export default Posts;
