import React, { Component } from 'react';
import axios from 'axios';
//import {Field} from "redux-form";
import { Form, Input, Button, TextArea } from 'semantic-ui-react';
import './Place.css';

class Write extends Component{
    state = {
        submited_title:"",
        submited_content:""
    }

    handleFormSubmit(_title,_content) {
       // event.preventDefault();
        /*
        const title=event.target.title.value;
        const content=event.target.content.value;
        */
        this.setState({
            submited_title:_title,
            submited_content:_content
        });

        console.log(this.state.submited_title);
        console.log(this.state.submited_content);
        
        /*
        switch(requestType){
            case 'post':
                */
                axios.post('http://127.0.0.1:8000/api/create/$', 
                {title:this.state.submited_title, content:this.state.submited_content})
                .then( res => { console.log(res) } )
                .catch( error => { console.error(error) } );
                
            /*case 'put':
                axios.post(`http://localhost:8000/api/${articleID}/`, 
                {title:title, content:content})
                .then( res => { console.log(res) } )
                .catch( error => { console.err(error) } );*/
        //}
    }

   
    
    render() {
        

        
        return (
            <div>
                <h1>write</h1>
                <div className="place">
                    <Form action="/" method="post"
                        onSubmit=
                        /*
                        {(event) => this.handleFormSubmit(
                            event,
                            this.props.requestType,
                            this.props.articleID
                        )}
                        */
                        
                        {function(e){
                        e.preventDefault();
                        this.handleFormSubmit(
                            //e,this.props.requestType,this.props.articleID
                            e.target.title.value,
                            e.target.content.value
                        );
                        }.bind(this)}
                        
                    >

                        <Form.Field>
                            <Form.Field
                                id='form-input-title'
                                control={Input}
                                placeholder='제목을 입력하세요'
                                name='title'
                            />

                            <TextArea 
                                id='form-input-content'
                                placeholder='Tell us more' 
                                style={{ minHeight: 300 }} 
                                name='content'
                            />

                        </Form.Field>
                        
                        <Form.Field
                            id='form-button'
                            control={Button}
                            content='저장'
                        />
            
                    </Form>
                </div>
          
            </div>
        );
    }
}

export default Write;
