import React, { Component } from 'react';
import axios from 'axios';
import './Posts.css';

import { Icon, Table, Pagination } from 'semantic-ui-react'

class Posts extends Component{
    
    state = {
        persons: [],
        activePage: [1],
        selected_content_id: null
    }

    
        
    async componentDidMount() {
        axios.get('http://127.0.0.1:8000/api')
            .then(res => {
            const persons = res.data;
            this.setState({ persons });
        })
    }
    
    render() {
        
        const tablelist = this.state.persons.map( (person) => 
            (
                <Table.Row >
                    <Table.Cell width='10'>
                        {person.title}
                    </Table.Cell>
                    <Table.Cell>
                        {person.content}
                    </Table.Cell>
                    <Table.Cell collapsing textAlign='right'>
                        {person.date}
                    </Table.Cell>
                </Table.Row>
            )
        );

        return (
            <div className="tablestyle">   
                <h1>posts</h1>
                
            <Pagination 
            defaultActivePage={5} 
            totalPages={10}
            onClick = {function(e) {
                e.preventDefault();
                
                console.log('e');
            }.bind(this)}
            /> 

            <div>
                <Table celled striped >
                    <Table.Header>
                    <Table.Row >
                        <Table.HeaderCell colSpan='5'>posts</Table.HeaderCell>
                    </Table.Row>
                    </Table.Header>

                    <Table.Body>
                        {tablelist}
                    </Table.Body>
                </Table>
                </div>
            </div>
        );
    }
}
export default Posts;
