import React, { Component } from 'react';
import axios from 'axios';

import { Icon, Table } from 'semantic-ui-react'

class Posts extends Component{
    
    state = {
        persons: []
    }

    async componentDidMount() {
        axios.get('http://127.0.0.1:8000/api')
            .then(res => {
            const persons = res.data;
            this.setState({ persons });
        })
    }
    
    render() {
        const tablelist = this.state.persons.map( (person) => 
            (
                <Table.Row >
                    <Table.Cell width='16'>
                        {person.title}
                    </Table.Cell>
                    <Table.Cell>
                        {person.content}
                    </Table.Cell>
                    <Table.Cell collapsing textAlign='right'>
                        {person.date}
                    </Table.Cell>
                </Table.Row>
            )
        );
        
        return (
            <div>   
                <h1>posts</h1>
                <div class='tablestyle'>
                <Table celled striped rowSpan="1">
                    <Table.Header class="four-hundred-width">
                    <Table.Row className="four-hundred-width">
                        <Table.HeaderCell rowSpan="1" colSpan="10">posts</Table.HeaderCell>
                    </Table.Row>
                    </Table.Header>

                    <Table.Body>
                        {tablelist}
                    </Table.Body>
                </Table>
                </div>
            </div>
        );
    }
}

export default Posts;
