                        }.bind(this)}>

                       
                            
                        <Form.Field>
                            <Form.Field
                                id='form-input-title'
                                control={Input}
                                placeholder='제목을 입력하세요'
                                name='title'
                                maxLength='20'
                            />
                            <select class="ui dropdown">
                                <option value="0">아파트 선택</option>
                                <option value="1" name="a1">아파트1</option>
                                <option value="2" name="a2">아파트2</option>
                            </select>

                            <TextArea
                                id='form-input-content'
                                placeholder='Tell us more' 
                                style={{ minHeight: 300 }} 
                                name='content'
                                maxLength='500'
                            />
                        </Form.Field>
                        
                        <Form.Field
                            id='form-button'
                            control={Button} 
                            type="submit"
                            content='저장'
                        />
                    </Form>we
                </div>
          
            </div>
        );
    }
}

export default Write;
