import React, { Component } from 'react';
import axios from 'axios';
import { Form, Input, Button } from 'semantic-ui-react';
import { Redirect } from 'react-router-dom';
import './Place.css';

class Write extends Component{
    state = {
        submited_status:false,
        submited_title:"",
        submited_content:""
    }

    handleFormSubmit(_title,_content) {
        this.setState({
            submited_title:_title,
            submited_content:_content
        });
        
    }

    
    render() {
        axios.post('http://127.0.0.1:8000/api/create/$', 
            {title:this.state.submited_title, content:this.state.submited_content})
            .then( res => { console.log(res) } )
            .catch( error => { console.error(error) } );

            if(this.state.submited_title==="") {
                this.setState({
                    submited_status:false
                })
            }
            
        return (

            <div>
                {
                    this.state.submited_status && <Redirect to="/saved"/>
                }
                <h1>write</h1>
                <div className="writeplace">  
                    <Form method="post"
                        onSubmit= {function(e){
                            e.preventDefault();
                            this.handleFormSubmit(
                                e.target.title.value,
                                e.target.content.value
                            );
                            this.setState({
                                
                                submited_status:true
                            })
                        }.bind(this)}>

                        <Form.Field>
                            <Form.Field
                                //error={{ content: "제목을 입력하지 않았습니다", pointing: 'below'}}
                                id='form-input-title'
                                control={Input}
                                placeholder='제목을 입력하세요'
                                name='title'
                            />

                            <Form.TextArea 
                                id='form-input-content'
                                placeholder='Tell us more' 
                                style={{ minHeight: 300 }} 
                                name='content'
                            />
                        </Form.Field>
                        
                        <Form.Field
                            id='form-button'
                            control={Button} 
                            type="submit"
                            content='저장'
                        />
                    </Form>
                </div>
          
            </div>
        );
    }
}

export default Write;
