import axios from 'axios';

import { Icon, Table } from 'semantic-ui-react'
const tt=[
        
]
class Posts extends Component{
    
    state = {
        persons: [],
        amount: null
    }

    async componentDidMount() {
        axios.get('http://127.0.0.1:8000/api')
            .then(res => {
            const persons = res.data;
            this.setState({ persons });
            var n=persons.length;
            this.setState({amount:n});
            console.log(n);
            console.log(this.state.amount);
        })
        
        
    }
    
    render() {
        const titlelist = this.state.persons.map( (person) => (<li>{person.title}</li>) );
        const tablelist = this.state.persons.map( (person) => (
            <Table.Row >
                <Table.Cell collapsing>
                   {person.title}
                </Table.Cell>
                <Table.Cell>
                    {person.content}
                </Table.Cell>
                <Table.Cell collapsing textAlign='right'>
                10 hours ago
                </Table.Cell>
            </Table.Row>
        )
        );
        const contentlist = this.state.persons.map( (person) => (<li>{person.content}</li>) );
        var i = 1;
        return (
            
            <div>
                
                <h1>posts</h1>
                
                <Table celled striped>
                    <Table.Header>
                    <Table.Row>
                        <Table.HeaderCell colSpan='3'>Git Repository</Table.HeaderCell>
                    </Table.Row>
                    </Table.Header>

                    <Table.Body>
                        {tablelist}
                        {/*}
                            <Table.Row >
                                <Table.Cell collapsing>
                                {this.state.persons.map( (person) => (<li>{person.title}</li>) )}
                                    {titlelist[i]}
                                </Table.Cell>
                                <Table.Cell>
                                    {contentlist[i]}
                                </Table.Cell>
                                <Table.Cell collapsing textAlign='right'>
                                10 hours ago
                                </Table.Cell>
                            </Table.Row>
                        */}
                    </Table.Body>
                </Table>
            </div>
        );
    }
}

export default Posts;
