import React, { Component } from 'react';
import axios from 'axios';
//import {Field} from "redux-form";
import { Form, Input, Button, TextArea } from 'semantic-ui-react';
import './Place.css';

class Write extends Component{
    state = {
        submited_title:"",
        submited_content:"",
        formFields:[]
    }

    onSubmit(_title,_content) {
        this.setState({
            submited_title:_title,
            submited_content:_content
        })
        axios.post('http://127.0.0.1:8000/api', {title:this.state.submited_title, content:this.state.submited_content})
            .then( response => { console.log(response) } )
            .catch( response => { console.log(response) } );
    }

    componentDidMount() {
        fetch('http://127.0.0.1:8000/api', (data) => {
            this.setState({
                formFields: data.fields // fields is an array
            });
        });
    }
    submitForm() {
        axios.post('http://127.0.0.1:8000/api', {/* put your updated fields' data here */}, (response) => {
           // check if things done successfully.
        });
    }
    render() {
        console.log(this.state.submited_title);
        console.log(this.state.submited_content);

        let fields = this.state.formFields.map((field) => {
            return (
                <input type="text" value={field.value} onChange={(newValue) => {/* update your  state here with new value */ }} name={field.name}/>
            )
        });
        return (
            <div>
                <h1>write</h1>
                <div className="place">
                    <Form action="/" method="post"
                        onSubmit={function(e){
                        e.preventDefault();
                        this.onSubmit(
                            e.target.title.value,
                            e.target.content.value
                        );
                        }.bind(this)}>

                        <Form.Field>
                            <Form.Field
                                id='form-input-title'
                                control={Input}
                                placeholder='제목을 입력하세요'
                                name='title'
                            />

                            <TextArea 
                                id='form-input-content'
                                placeholder='Tell us more' 
                                style={{ minHeight: 300 }} 
                                name='content'
                            />

                        </Form.Field>
                        
                        <Form.Field
                            id='form-button'
                            control={Button}
                            content='저장'
                            
                            /*onSubmit={function(e) {
                                this.onSubmit(
                                    e.target.title.value,
                                    e.target.content.value
                                );
                            }.bind(this)}*/
                        />
                            
   
                        <form action="">
                {fields}
                <button onClick={function(){this.submitForm()}.bind(this)}>Save</button>
            </form>
                    </Form>
                </div>
          
            </div>
        );
    }
}

export default Write;
