import React, { Component } from 'react';
import { Router, Route, Redirect } from 'react-router-dom';
import Poststable from './Poststable';
import Detail from './Detail';
import './Place.css';

class Posts extends Component{
    state={
        bar:"posts",
        mode:"read",
        id:null,
        title:null,
        content:null
    }
    render() { 
        return (
            <div>   
                <h1>{this.state.bar}</h1>
 
                <div className="place">
                    {
                        this.state.mode==="read" && <Poststable onDetailShow={function(_id,_title,_content){
                            console.log(_id);
                            console.log(_title);
                            this.setState({
                                mode:"update",
                                bar:"detail"
                            });
                        }.bind(this)} >
                        </Poststable>
                    }
                    {
                        this.state.mode==="update" && <Detail ></Detail>
                    }

                </div>
                
            </div>
        );
    }
}
export default Posts;
